execution.script:
node {
    /*
    currentBuild.displayName="${Rama_Desarrollo}"
    currentBuild.description="Pipeline de prueba proyecto BLENDER"

    def m2="/sqa/apache-maven-3.3.9/bin"
    */
        stage('Checkout') {
            /* 
            Se realiza el checkout del repositorio de GIT. Se proporciona un parámetro
            de entrada que será una etiqueta o rama para ejecutar. 
          */
            echo 'Pipeline Checkout finalizado correctamente'
        }
        stage('Build') {
            /* 
            Se realiza la contrucción del proyecto y test unitarios. Los parámetros
            de uso general se han declarado en environment for the run

             sh "${m2}/mvn clean install -U -Denv-pro=true -Dmaven.test.skip=true -Dusuario=${user} -Dclave=${password}"
            */
            echo 'Pipeline Build finalizado correctamente'
        }
        stage('Quality') {
            /* 
            Se realiza la revisión de código en el proyecto. Los parámetros de uso
            general se han declarado en environment for the run

             sh "${m2}/mvn sonar:sonar -P sonar -Denv-pro=true -Dmaven.test.skip=true -Dusuario=${user} -Dclave=${password} -Dsonar.login=${user} -Dsonar.password=${password}"
            */
            echo 'Pipeline Quality finalizado correctamente'
        }

        stage('Tests') {
            echo 'Pipeline Tests finalizado correctamente'
        }

        stage('DeployNexus') {
            /*
            Se realiza la subida de los Artefactos a Nexus. Los parámetros de uso 
            general se han declarado en environment for the run

            sh "${m2}/mvn deploy -Denv-pro=true -Dmaven.test.skip=true -Dusuario=${user} -Dclave=${password}"
            */
            echo 'Pipeline Deploy_Nexus finalizado correctamente'   
        }
        stage('DeployApp') {

            echo 'Pipeline DeployApp finalizado correctamente'  
        }
}
